import { useActions } from "../hooks/use-actions";
import "./add-cell.css";

interface AddCellProps {
  previousCellId: string | null;
  addClass?: string;
}

const AddCell: React.FC<AddCellProps> = ({ previousCellId, addClass }) => {
  const { insertCellAfter } = useActions()

  return (
    <div className={`add-cell ${addClass}`}>
      <button onClick={() => insertCellAfter(previousCellId ,'text')}>+ Text</button>
      <button onClick={() => insertCellAfter(previousCellId ,'code')}>+ Code</button>
    </div>
  );
};

export default AddCell;
